    "\n",
    "deposits <- read.csv(\"/kaggle/input/microfinance-data/deposits_microfinance.csv\")\n",
    "colnames(deposits)\n",
    "\n",
    "loans <- read.csv(\"/kaggle/input/microfinance-data/loans_microfinance.csv\")\n",
    "colnames(loans)\n"
   ]
