    "# What is the average loan amount disbursed based on client age group, income level, or gender?\n",
    "# step 1: load r packages\n",
    "library(tidyverse)\n",
    "library(dplyr)\n",
    "library(ggplot2)\n",
    "\n",
    "# step 2: create age groups\n",
    "age_groups <- cut(clients$Age,\n",
    "                 breaks=c(18,25,35,45,55,65),\n",
    "                 labels=c(\"18-25\",\"26-35\",\"36-45\",\"46-55\",\"56-65\"),\n",
    "                 include.lowest=TRUE)\n",
    "\n",
    "# step 3: create an age group column using mutate function\n",
    "clients <- clients %>%\n",
    "mutate(Age_Groups = age_groups)\n",
    "\n",
    "# step 4: create income groups\n",
    "income_groups <- cut(clients$Income_Level,\n",
    "                    breaks=c(500,2500,4500,6500,8500,10000),\n",
    "                    labels=c(\"500-2500\",\"2501-4500\",\"4501-6500\",\"6501-8500\",\"8501-10000\"),\n",
    "                    include.lowest=TRUE)\n",
    "\n",
    "# step 5: create an income group column using mutate function\n",
    "clients <- clients %>%\n",
    "mutate(Income_Groups = income_groups)\n",
    "\n",
    "# step 6: join the tables\n",
    "loans_clients <- left_join(loans,clients,by='Client_ID')\n",
    "\n",
    "# step 7: calculate the average loans disbursed by demographics\n",
    "# step 6a: calculate average loans disbursed by age group\n",
    "loans_agegroups <- loans_clients %>%\n",
    "group_by (Age_Groups)%>%\n",
    "summarise(avg_loans = mean(Loan_Amount,na.rm=TRUE),groups='drop')\n",
    "print(loans_agegroups)\n",
    "\n",
    "# step 7b: calculate the average loans disbursed by income\n",
    "loans_income <- loans_clients %>%\n",
    "group_by(Income_Groups)%>%\n",
    "summarise(avg_loans =  mean(Income_Level,na.rm=TRUE),.groups='drop')\n",
    "print(loans_income)\n"
   ]
  },
