   "source": [
    "# What percentage of loans are in \"Active,\" \"Paid Off,\" or \"Defaulted\" status, and how do these rates vary by loan term?\n",
    "\n",
    "# step1: establish the loan status count and percentage count\n",
    "loan_status <- loans %>%\n",
    "group_by(Status,Loan_Term)%>%\n",
    "summarise(\n",
    "    status_count = n()\n",
    "        ,.groups='drop')%>%\n",
    "mutate(percentage_status=status_count/sum(status_count)*100)\n",
    "print(loan_status)\n",
    "\n",
    "#step 2: visualize the results above\n",
    "ggplot(data=loan_status, mapping=aes(x=Loan_Term, y=percentage_status, fill=Status))+geom_col()+\n",
    "labs(title='Percentage Loan Status by Loan Term',\n",
    "    x = 'Loan Term',\n",
    "    y = 'Percentage Status')\n"
   ]
