   "source": [
    "# How does the interest rate on loans influence the outstanding loan balances?\n",
    "\n",
    "# APPROACH I\n",
    "#step 1: create loan balance group\n",
    "loan_balance_category <- cut(loans$Outstanding_Balance,\n",
    "                            breaks=c(3,10000,20000,30000,40000,50000),\n",
    "                            labels=c(\"3-10000\",\"10001-20000\",\"20001-30000\",\"30001-40000\",\"40001-50000\"),\n",
    "                            include.lowest=TRUE)\n",
    "\n",
    "#step 2: create a loan balance group column using mutate function\n",
    "loans <- loans %>%\n",
    "mutate(Loan_Balance_Category = loan_balance_category)\n",
    "\n",
    "#step3: establish the relationship between interest rate and outstanding loan balances\n",
    "interest_outstanding <- loans %>%\n",
    "group_by (Loan_Balance_Category) %>%\n",
    "summarise(avg_interest = mean(Interest_Rate,na.rm=TRUE),.groups='drop')\n",
    "print(interest_outstanding)\n",
    "\n",
    "# APPROACH II\n",
    "# calculate the correlation between outstanding loan balance and interest rates\n",
    "correlation  <- cor(loans$Outstanding_Balance,loans$Interest_Rate,use='complete.obs')\n",
    "print(correlation)\n"
   ]
