    "# What is the average savings account balance across different client demographics?\n",
    "\n",
    "# step1: join the deposit and clients tables\n",
    "deposit_client <-left_join(deposits, clients,by='Client_ID')\n",
    "\n",
    "# step2: calculate the average savings and group by demographics (age and gender)\n",
    "savings_balance <- deposit_client %>%\n",
    "group_by(Age_Groups,Gender)%>%\n",
    "summarise(avg_savings=mean(Savings_Account_Balance,na.rm=TRUE),.groups='drop')\n",
    "print(savings_balance)\n",
    "\n",
    "# step3:visualize the relationship\n",
    "ggplot(data=savings_balance,mapping=aes(x=Age_Groups,y=avg_savings,fill=Gender))+geom_col(position='stack')+\n",
    "labs(title='Savings Balance by Age Groups and Gender')\n"
   ]
