    "# How frequently do clients make deposits (weekly vs. monthly), and what is the average deposit value by frequency?\n",
    "\n",
    "# calculate the deposit frequency\n",
    "deposit_frequency <- deposits %>%\n",
    "group_by (Deposit_Frequency)%>%\n",
    "summarise(frequency_count=n(),.groups='drop')%>%\n",
    "arrange(desc(frequency_count))\n",
    "print(deposit_frequency)\n",
    "\n",
    "# calculate the average deposit value by frequency\n",
    "avg_deposit_value <- deposits %>%\n",
    "group_by (Deposit_Frequency)%>%\n",
    "summarise(avg_deposit=mean(Deposit_Value))\n"
   ]
