    "# Do business clients tend to take higher-value loans and make larger deposits compared to individual clients?\n",
    "#step1: left join the clients table to the loans table\n",
    "clients_loans <- left_join(clients,loans,by='Client_ID')\n",
    "\n",
    "#step2: let join the clients loans table to the deposit table\n",
    "clients_loans_deposits <- left_join(clients_loans, deposits, by='Client_ID',relationship='many-to-many')\n",
    "\n",
    "#step2: establish the relationship business client and loans and deposits\n",
    "\n",
