   "source": [
    "# How does the loan default rate vary by client income level, education level, or age?\n",
    "loan_default <- loans_clients %>%\n",
    "filter(!is.na(Status) & Status=='Defaulted')%>%\n",
    "group_by(Income_Groups)%>%\n",
    "summarise(default_count=n(),.groups='drop')%>%\n",
    "mutate(default_percentage=default_count/sum(default_count)*100)\n",
    "print(loan_default)\n",
    "\n",
    "# Visualize results\n",
    "ggplot(data=loan_default,mapping=aes(x=Income_Groups,y=default_percentage))+geom_col()+\n",
    "labs(title='How loan default rate varies by client Income') + theme_minimal()\n"
   ]
  },
