   "source": [
    "# What is the average interest rate offered on deposits across different deposit types?\n",
    "\n",
    "#Step1: join the loans clients and deposits table using the clients table\n",
    "loans_clients_deposits <- left_join(loans_clients,deposits,by='Client_ID',relationship='many-to-many')\n",
    "\n",
    "#step2: establish the average rates by deposit account\n",
    "deposit_interest <- loans_clients_deposits %>%\n",
    "filter(!is.na(Deposit_Type))%>%\n",
    "group_by(Deposit_Type)%>%\n",
    "summarise(avg_interest=mean(Interest_Rate),.groups='drop')\n",
    "print(deposit_interest)\n"
   ]
  },
