   "source": [
    "# How do withdrawal patterns impact net savings growth? Are clients who withdraw frequently less likely to grow their savings?\n",
    "\n",
    "# step1: calculate the correlation between withdrawals and net savings\n",
    "correlation <- cor(deposits$Withdrawals,deposits$Net_Savings_Growth)\n",
    "print(correlation)\n",
    "\n",
    "# step2: establish impact of frequent withdrawers on savings growth\n",
    "frequent_withdrawers <- deposits %>%\n",
    "group_by (Client_ID)%>%\n",
    "summarise(\n",
    "    total_withdrawals=sum(Withdrawals),\n",
    "    total_net_savings=sum(Net_Savings_Growth),\n",
    ".groups='drop')%>%\n",
    "arrange(desc(total_withdrawals))%>%\n",
    "slice(1:5)\n",
    "print(frequent_withdrawers)\n",
    "\n",
    "#step2a: visualize the results\n",
    "ggplot(data=frequent_withdrawers,mapping=aes(x=total_withdrawals,y=total_net_savings,fill=Client_ID))+\n",
    "geom_col(position='dodge')\n",
    "\n",
    "# step 3: establish impact of infrequent withdrawers on savings growth\n",
    "infrequent_withdrawers <- deposits %>%\n",
    "group_by (Client_ID)%>%\n",
    "summarise(\n",
    "    total_withdrawals=sum(Withdrawals,na.rm=TRUE),\n",
    "    total_net_savings=sum(Net_Savings_Growth,na.rm=TRUE),\n",
    ".groups='drop')%>%\n",
    "arrange(total_withdrawals)%>%\n",
    "slice(1:5)\n",
    "print(infrequent_withdrawers)\n",
    "\n",
    "#step3a: visualize the results\n",
    "ggplot(data=infrequent_withdrawers,mapping=aes(x=total_withdrawals,y=total_net_savings,fill=Client_ID))+\n",
    "geom_col(position='dodge')\n"
   ]
