   "source": [
    "# Is there a seasonality pattern in loan disbursements? Are more loans issued in certain months of the year?\n",
    "seasonality_pattern <- loans %>%\n",
    "mutate(Disbursement_Month=format(as.Date(Disbursement_Date),\"%Y-%m\"))%>%\n",
    "group_by(Disbursement_Month)%>%\n",
    "summarise(loan_count=n(),.groups='drop')%>%\n",
    "arrange(desc(loan_count))%>%\n",
    "slice(1:10)\n",
    "print(seasonality_pattern)\n",
    "\n",
    "# visualization of the pattern\n",
    "ggplot(data=seasonality_pattern,mapping=aes(x=Disbursement_Month,y=loan_count))+geom_col(fill='lightblue')+\n",
    "labs(title='Seasonality Pattern in Loan Disbursements')+theme_minimal()+ \n",
    "theme(axis.text.x = element_text(angle = 45, hjust = 1))\n"
   ]
  }
